import "regenerator-runtime/runtime";

const container = document.querySelector(".container");
const modal = document.querySelector(".modal");
const modalContainer = document.querySelector(".modal__container");
const input = document.querySelector(".search-field");
const select = document.querySelector(".drop-list");

let searchLetter = "";

const countriesData = fetch(
	"https://restcountries.eu/rest/v2/all?fields=alpha3Code;flag;name;capital;region;population"
)
	.then((response) => response.json())
	.then((data) => data);

(async () => {
	renderCountry(await countriesData);
})();

const searchFilter = async () => {
	const filtered = await countriesData;

	const shit = filtered.filter((data) =>
		data.name.toLowerCase().includes(searchLetter.toLowerCase())
	);

	console.log(shit);
	renderCountry(shit);
};

const selectRegion = async (region) => {
	const selectRegion = await countriesData;
	const shit = selectRegion.filter((data) => data.region === region);
	container.innerHTML = "";
	renderCountry(shit);
};

select.addEventListener("change", (e) => {
	input.value = "";
	const region = e.target.value;

	selectRegion(region);
});

input.addEventListener("input", (e) => {
	select.value = "";

	searchLetter = e.target.value;
	const validate = /^[a-zA-Z]+$/;
	if (e.target.value.match(validate)) {
		container.innerHTML = "";
		searchFilter();
	}
});

function renderCountry(data) {
	const renderCard = data.forEach((data) => {
		const countryCard = document.createElement("div");
		countryCard.classList.add("container__card");

		countryCard.innerHTML = `
		<div class="country-card" data-code=${data.alpha3Code}>
		<div class="country-card__wrapper">
		<img src=${data.flag} alt="flag" class="country-card__flag">
		</div>
		 
			
            <div class="country-card__info">
                <h3 class="name">${data.name}</h3>
                <p class="population">Population:${formatNumber(
									data.population
								)}</p>
                <p class="region">Region:${data.region}</p>
                <p class="capital">Capital:${data.capital}</p>
		</div>
		`;

		container.appendChild(countryCard);
	});
}

document.addEventListener("click", (e) => {
	if (e.target.className === "country-card__flag") {
		const testshit = e.target.parentElement.parentElement.dataset.code;

		handleCountryClick(testshit);
	}
});

function openModal(countryData) {
	const {
		name,
		nativeName,
		population,
		region,
		subregion,
		capital,
		topLevelDomain,
		currencies,
		languages,
		borders,
		flag,
	} = countryData;

	modalContainer.innerHTML = `
	  <img src=${flag} alt="" class="modal__image">
                <h2 class="modal__country">${name}</h2>

                <p class="modal__native">Native Name: ${nativeName}</p>
                <p class="modal__population">Population: ${formatNumber(
									population
								)}</p>
                <p class="modal__region">Region: ${region}</p>
                <p class="modal__subregion">Sub Region: ${subregion}</p>
                <p class="modal__capital">Capital: ${capital}</p>
                <p class="modal__domain">Top Level Domain: ${topLevelDomain}</p>
                <p class="modal__currencies">Currencies: ${currencies.map(
									(currency) => currency.code
								)}</p>
                <p class="modal__languages">Languages: ${languages.map(
									(language) => language.name
								)}</p>
				
	`;

	modal.appendChild(modalContainer);
}

async function handleCountryClick(countryCode) {
	const countryData = await getCountryDetails(countryCode);

	modal.style.display = "flex";

	console.log(countryData);
	openModal(countryData);
}

async function getCountryDetails(countryCode) {
	const rawCountryData = await fetch(
		`https://restcountries.eu/rest/v2/alpha/${countryCode}?fields=name;nativeName;population;region;subregion;capital;topLevelDomain;currencies;languages;borders;flag`
	);
	const countryData = await rawCountryData.json();

	return countryData;
}

function formatNumber(num) {
	return num.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
}
